package com.example.myapplication.ui

import android.content.Intent
import android.graphics.Color
import android.os.Bundle
import android.os.Handler
import android.os.Looper
import android.os.Build
import android.os.VibrationEffect
import android.os.Vibrator
import android.os.VibratorManager
import android.view.Gravity
import android.view.View
import android.widget.ImageButton
import android.widget.ImageView
import android.widget.TableLayout
import android.widget.TableRow
import android.widget.TextView
import android.widget.Toast
import androidx.appcompat.app.AppCompatActivity
import com.bumptech.glide.Glide
import com.example.myapplication.R
import com.example.myapplication.logic.GameManager

class MainActivity : AppCompatActivity() {

    // ===== Grid Configuration =====
    private val GRID_ROWS = 8  // Number of rows in grid
    private val GRID_COLS = 3  // Number of columns (lanes)
    private val PLAYER_ROW = GRID_ROWS - 1  // Player is always in bottom row

    // ===== UI Variables =====
    private lateinit var mainLayout: android.widget.RelativeLayout
    private lateinit var gameGrid: TableLayout
    private lateinit var btnLeft: ImageButton
    private lateinit var btnRight: ImageButton
    private lateinit var textScore: TextView
    private lateinit var heartViews: Array<ImageView>

    // ===== Grid State (2D Matrix) =====
    private val gridCells = Array(GRID_ROWS) { Array<ImageView?>(GRID_COLS) { null } }
    private val gridState = Array(GRID_ROWS) { IntArray(GRID_COLS) { 0 } }  // 0=empty, 1=obstacle, 2=player

    // ===== Logic =====
    private lateinit var gameManager: GameManager
    private val handler = Handler(Looper.getMainLooper())
    private val frameRate: Long = 500 // Move every 500ms (discrete jumps)

    // Obstacle tracking
    private var obstacleRow = 0
    private var obstacleCol = 1  // Start in center column
    private var collisionDetected = false

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_main_grid)

        // Force LTR layout direction
        window.decorView.layoutDirection = View.LAYOUT_DIRECTION_LTR

        initViews()
        buildGrid()
        initGame()
    }

    // ===== View Initialization =====
    private fun initViews() {
        mainLayout = findViewById(R.id.main_layout)
        gameGrid = findViewById(R.id.game_grid)
        btnLeft = findViewById(R.id.btn_left)
        btnRight = findViewById(R.id.btn_right)
        textScore = findViewById(R.id.text_score)

        // Load animated GIF background
        val backgroundGif = findViewById<ImageView>(R.id.background_gif)
        Glide.with(this)
            .asGif()
            .load(R.drawable.tenor)
            .into(backgroundGif)

        heartViews = arrayOf(
            findViewById(R.id.heart1),
            findViewById(R.id.heart2),
            findViewById(R.id.heart3)
        )
    }

    // ===== Build Grid Dynamically =====
    private fun buildGrid() {
        gameGrid.removeAllViews()

        for (row in 0 until GRID_ROWS) {
            val tableRow = TableRow(this).apply {
                layoutParams = TableLayout.LayoutParams(
                    TableLayout.LayoutParams.MATCH_PARENT,
                    0,
                    1f  // Equal weight for all rows
                )
            }

            for (col in 0 until GRID_COLS) {
                val cell = ImageView(this).apply {
                    layoutParams = TableRow.LayoutParams(
                        0,
                        TableRow.LayoutParams.MATCH_PARENT,
                        1f  // Equal weight for all columns
                    )
                    scaleType = ImageView.ScaleType.FIT_CENTER
                    setPadding(8, 8, 8, 8)

                    // Debug: Show grid borders (optional - comment out for production)
                    // setBackgroundColor(Color.argb(50, 255, 255, 255))
                }

                gridCells[row][col] = cell
                tableRow.addView(cell)
            }

            gameGrid.addView(tableRow)
        }
    }

    private fun initGame() {
        gameManager = GameManager()

        // Place player in center of bottom row
        placePlayer()

        // Place first obstacle
        placeObstacle()

        btnLeft.setOnClickListener {
            gameManager.moveCarLeft()
            updatePlayerPosition()
        }

        btnRight.setOnClickListener {
            gameManager.moveCarRight()
            updatePlayerPosition()
        }

        updatePlayerPosition()
        updateLivesUI()
        updateScoreUI()
        startTimer()
    }

    // ===== Place Player in Grid =====
    private fun placePlayer() {
        // Clear previous player position
        for (col in 0 until GRID_COLS) {
            gridState[PLAYER_ROW][col] = 0
            gridCells[PLAYER_ROW][col]?.setImageDrawable(null)
        }

        // Place player in current column
        val playerCol = gameManager.currentCarIndex
        gridState[PLAYER_ROW][playerCol] = 2
        gridCells[PLAYER_ROW][playerCol]?.setImageResource(R.drawable.bitcoin)
    }

    // ===== Place Obstacle in Grid =====
    private fun placeObstacle() {
        obstacleRow = 0
        obstacleCol = (0 until GRID_COLS).random()
        gridState[obstacleRow][obstacleCol] = 1
        gridCells[obstacleRow][obstacleCol]?.setImageResource(R.drawable.bear)
        collisionDetected = false
    }

    // ===== Game Loop =====
    private val runnable = object : Runnable {
        override fun run() {
            handler.postDelayed(this, frameRate)
            tick()
        }
    }

    private fun startTimer() {
        handler.post(runnable)
    }

    private fun stopTimer() {
        handler.removeCallbacks(runnable)
    }

    // ===== Game Tick (Discrete Grid Movement) =====
    private fun tick() {
        if (!gameManager.isGameRunning) {
            stopTimer()
            openGameOverActivity()
            return
        }

        moveObstacleInGrid()
        checkCollisionInGrid()
        updateLivesUI()
        updateScoreUI()
    }

    // ===== Grid-based Obstacle Movement =====
    private fun moveObstacleInGrid() {
        // Clear current obstacle position
        gridCells[obstacleRow][obstacleCol]?.setImageDrawable(null)
        gridState[obstacleRow][obstacleCol] = 0

        // Move obstacle down one row (discrete jump)
        obstacleRow++

        // Check if obstacle reached bottom
        if (obstacleRow >= GRID_ROWS) {
            // Obstacle successfully passed - check if dodged
            if (!collisionDetected) {
                gameManager.incrementScore()
            }

            // Reset obstacle to top
            placeObstacle()
            return
        }

        // Place obstacle in new position
        gridState[obstacleRow][obstacleCol] = 1
        gridCells[obstacleRow][obstacleCol]?.setImageResource(R.drawable.bear)
    }

    // ===== Grid-based Collision Detection =====
    private fun checkCollisionInGrid() {
        if (collisionDetected) return

        // Check if obstacle is in same position as player
        val playerCol = gameManager.currentCarIndex

        if (obstacleRow == PLAYER_ROW && obstacleCol == playerCol) {
            collisionDetected = true
            onCrash()
            gameManager.checkCollision(obstacleCol)
        }
    }

    // ===== UI Updates =====
    private fun updatePlayerPosition() {
        placePlayer()
    }

    private fun updateLivesUI() {
        for (i in heartViews.indices) {
            heartViews[i].visibility = if (i < gameManager.lives) View.VISIBLE else View.INVISIBLE
        }
    }

    private fun updateScoreUI() {
        textScore.text = getString(R.string.score_label, gameManager.score)
    }

    private fun openGameOverActivity() {
        val intent = Intent(this, GameOverActivity::class.java)
        intent.putExtra("FINAL_SCORE", gameManager.score)
        startActivity(intent)
        finish()
    }

    // ===== Crash Handler =====
    private fun onCrash() {
        Toast.makeText(this, "Crash!", Toast.LENGTH_SHORT).show()

        val vibrator = if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.S) {
            val vibratorManager = getSystemService(VIBRATOR_MANAGER_SERVICE) as VibratorManager
            vibratorManager.defaultVibrator
        } else {
            @Suppress("DEPRECATION")
            getSystemService(VIBRATOR_SERVICE) as Vibrator
        }
        vibrator.vibrate(VibrationEffect.createOneShot(500, VibrationEffect.DEFAULT_AMPLITUDE))
    }

    override fun onPause() {
        super.onPause()
        stopTimer()
    }

    override fun onResume() {
        super.onResume()
        if (::gameManager.isInitialized && gameManager.isGameRunning) {
            startTimer()
        }
    }
}

package com.example.myapplication.ui

import android.content.Intent
import android.os.Bundle
import android.os.Handler
import android.os.Looper
import android.os.Build
import android.os.VibrationEffect
import android.os.Vibrator
import android.os.VibratorManager
import android.view.View
import android.view.ViewGroup
import android.widget.ImageButton
import android.widget.ImageView
import android.widget.TableLayout
import android.widget.TableRow
import android.widget.TextView
import android.widget.Toast
import androidx.appcompat.app.AppCompatActivity
import com.bumptech.glide.Glide
import com.example.myapplication.R
import com.example.myapplication.logic.GameManager

class MainActivity : AppCompatActivity() {

    // ===== Grid Configuration =====
    private val GRID_ROWS = 7
    private val GRID_COLS = 3
    private val PLAYER_ROW = GRID_ROWS - 1

    // ===== UI Variables =====
    private lateinit var gameGrid: TableLayout
    private lateinit var btnLeft: ImageButton
    private lateinit var btnRight: ImageButton
    private lateinit var textScore: TextView
    private lateinit var heartViews: Array<ImageView>

    // ===== Grid Cells =====
    private val gridCells = Array(GRID_ROWS) { row ->
        Array<ImageView?>(GRID_COLS) { null }
    }

    // ===== Logic =====
    private lateinit var gameManager: GameManager
    private val handler = Handler(Looper.getMainLooper())
    private val frameRate: Long = 600 // Move every 0.6 seconds

    // Obstacle tracking
    private var obstacleRow = -1
    private var obstacleCol = 1
    private var collisionDetected = false

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_main)

        // Force LTR
        window.decorView.layoutDirection = View.LAYOUT_DIRECTION_LTR

        initViews()
        buildGrid()
        initGame()
    }

    private fun initViews() {
        gameGrid = findViewById(R.id.game_grid)
        btnLeft = findViewById(R.id.btn_left)
        btnRight = findViewById(R.id.btn_right)
        textScore = findViewById(R.id.text_score)

        // Load GIF background
        val backgroundGif = findViewById<ImageView>(R.id.background_gif)
        Glide.with(this)
            .asGif()
            .load(R.drawable.tenor)
            .into(backgroundGif)

        heartViews = arrayOf(
            findViewById(R.id.heart1),
            findViewById(R.id.heart2),
            findViewById(R.id.heart3)
        )
    }

    private fun buildGrid() {
        gameGrid.removeAllViews()

        // Calculate cell size
        val displayMetrics = resources.displayMetrics
        val screenWidth = displayMetrics.widthPixels
        val screenHeight = displayMetrics.heightPixels

        val availableHeight = screenHeight - 400 // Leave space for UI
        val cellHeight = availableHeight / GRID_ROWS
        val cellWidth = (screenWidth - 80) / GRID_COLS

        for (row in 0 until GRID_ROWS) {
            val tableRow = TableRow(this).apply {
                layoutParams = TableLayout.LayoutParams(
                    TableLayout.LayoutParams.MATCH_PARENT,
                    cellHeight
                )
            }

            for (col in 0 until GRID_COLS) {
                val cell = ImageView(this).apply {
                    layoutParams = TableRow.LayoutParams(
                        cellWidth,
                        ViewGroup.LayoutParams.MATCH_PARENT
                    )
                    scaleType = ImageView.ScaleType.FIT_CENTER
                    setPadding(4, 4, 4, 4)
                }

                gridCells[row][col] = cell
                tableRow.addView(cell)
            }

            gameGrid.addView(tableRow)
        }
    }

    private fun initGame() {
        gameManager = GameManager()

        btnLeft.setOnClickListener {
            gameManager.moveCarLeft()
            updatePlayerPosition()
        }

        btnRight.setOnClickListener {
            gameManager.moveCarRight()
            updatePlayerPosition()
        }

        updatePlayerPosition()
        updateLivesUI()
        updateScoreUI()
        spawnNewObstacle()
        startTimer()
    }

    // ===== Game Loop =====
    private val runnable = object : Runnable {
        override fun run() {
            if (gameManager.isGameRunning) {
                handler.postDelayed(this, frameRate)
                tick()
            }
        }
    }

    private fun startTimer() {
        handler.postDelayed(runnable, frameRate)
    }

    private fun stopTimer() {
        handler.removeCallbacks(runnable)
    }

    private fun tick() {
        if (!gameManager.isGameRunning) {
            stopTimer()
            openGameOverActivity()
            return
        }

        moveObstacle()
        updateScoreUI()
        updateLivesUI()
    }

    private fun spawnNewObstacle() {
        obstacleRow = 0
        obstacleCol = (0 until GRID_COLS).random()
        collisionDetected = false

        // Place obstacle in grid
        gridCells[obstacleRow][obstacleCol]?.setImageResource(R.drawable.bear)
    }

    private fun moveObstacle() {
        if (obstacleRow < 0) return

        // Clear current position
        if (obstacleRow < GRID_ROWS) {
            gridCells[obstacleRow][obstacleCol]?.setImageDrawable(null)
        }

        // Move down one row
        obstacleRow++

        // Check if reached bottom
        if (obstacleRow >= GRID_ROWS) {
            // Obstacle passed successfully
            if (!collisionDetected) {
                gameManager.incrementScore()
            }
            spawnNewObstacle()
            return
        }

        // Check collision BEFORE placing
        if (obstacleRow == PLAYER_ROW && obstacleCol == gameManager.currentCarIndex) {
            // Collision!
            collisionDetected = true
            onCrash()
            gameManager.checkCollision(obstacleCol)

            // Skip placing obstacle (it crashed)
            spawnNewObstacle()
            return
        }

        // Place obstacle in new position
        gridCells[obstacleRow][obstacleCol]?.setImageResource(R.drawable.bear)
    }

    private fun updatePlayerPosition() {
        // Clear all player positions
        for (col in 0 until GRID_COLS) {
            gridCells[PLAYER_ROW][col]?.setImageDrawable(null)
        }

        // Place player in current lane
        val playerCol = gameManager.currentCarIndex
        gridCells[PLAYER_ROW][playerCol]?.setImageResource(R.drawable.bitcoin)
    }

    private fun updateLivesUI() {
        for (i in heartViews.indices) {
            heartViews[i].visibility = if (i < gameManager.lives) View.VISIBLE else View.INVISIBLE
        }
    }

    private fun updateScoreUI() {
        textScore.text = getString(R.string.score_label, gameManager.score)
    }

    private fun onCrash() {
        Toast.makeText(this, "Crash!", Toast.LENGTH_SHORT).show()

        val vibrator = if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.S) {
            val vibratorManager = getSystemService(VIBRATOR_MANAGER_SERVICE) as VibratorManager
            vibratorManager.defaultVibrator
        } else {
            @Suppress("DEPRECATION")
            getSystemService(VIBRATOR_SERVICE) as Vibrator
        }
        vibrator.vibrate(VibrationEffect.createOneShot(300, VibrationEffect.DEFAULT_AMPLITUDE))
    }

    private fun openGameOverActivity() {
        val intent = Intent(this, GameOverActivity::class.java)
        intent.putExtra("FINAL_SCORE", gameManager.score)
        startActivity(intent)
        finish()
    }

    override fun onPause() {
        super.onPause()
        stopTimer()
    }

    override fun onResume() {
        super.onResume()
        if (::gameManager.isInitialized && gameManager.isGameRunning) {
            startTimer()
        }
    }
}
